package com.inhelpimport android.app.Applicationimport android.os.StrictModeimport com.inhelp.instagram.di.noCropModuleimport com.inhelp.di.appModuleimport com.inhelp.gallery.di.galleryModuleimport com.inhelp.instagram.grid.di.gridModuleimport com.inhelp.instagram.panorama.di.panoramaModuleimport com.inhelp.dialogs.di.tagsModuleimport com.inhelp.dialogs.main.DialogManagerimport com.inhelp.text_style.di.textStyleModuleimport org.koin.android.ext.koin.androidContextimport org.koin.core.context.startKoinimport java.lang.ref.WeakReferenceclass MainApp : Application() {    override fun onCreate() {        super.onCreate()        StrictMode.setVmPolicy(StrictMode.VmPolicy.Builder().build())        startKoin{            androidContext(this@MainApp)            modules(listOf(                    appModule,                    textStyleModule,                    tagsModule,                    galleryModule,                    noCropModule,                    gridModule,                    panoramaModule))        }    }}