package com.inhelpimport android.app.Applicationimport android.os.StrictModeimport com.inhelp.instagram.di.cropModuleimport com.inhelp.di.appModuleimport com.inhelp.gallery.di.galleryModuleimport com.inhelp.grids.di.gridModuleimport com.inhelp.panorama.di.panoramaModuleimport com.inhelp.tags.di.tagsModuleimport com.inhelp.text_style.di.textStyleModuleimport org.koin.android.ext.koin.androidContextimport org.koin.core.context.startKoinclass MainApp : Application() {    override fun onCreate() {        super.onCreate()        StrictMode.setVmPolicy(StrictMode.VmPolicy.Builder().build())        startKoin{            androidContext(this@MainApp)            modules(listOf(                    appModule,                    textStyleModule,                    tagsModule,                    galleryModule,                    cropModule,                    panoramaModule,                    gridModule))        }    }}