package ua.com.composeimport android.app.Applicationimport android.content.Contextimport ua.com.compose.gallery.di.galleryModuleimport org.koin.android.ext.koin.androidContextimport org.koin.core.context.startKoinimport ua.com.compose.analytics.AnalyticsImplimport ua.com.compose.analytics.analyticsimport ua.com.compose.config.RemoteConfigImplimport ua.com.compose.config.remoteConfigimport ua.com.compose.di.appModuleimport ua.com.compose.extension.prefsimport ua.com.compose.file_storage.FileStorageimport ua.com.compose.other_color_pick.di.otherColorPickModuleclass MainApp : Application() {    override fun onCreate() {        super.onCreate()        prefs = getSharedPreferences("compose", Context.MODE_PRIVATE)        analytics = AnalyticsImpl(context = applicationContext)        remoteConfig = RemoteConfigImpl()        FileStorage.init(applicationContext)        startKoin{            androidContext(this@MainApp)            modules(listOf(                    appModule,                    galleryModule,                    otherColorPickModule            ))        }    }}