package ua.com.composeimport android.app.Applicationimport android.content.Contextimport org.koin.android.ext.koin.androidContextimport org.koin.core.context.startKoinimport ua.com.compose.api.analytics.AnalyticsImplimport ua.com.compose.api.analytics.analyticsimport ua.com.compose.api.config.RemoteConfigImplimport ua.com.compose.api.config.remoteConfigimport ua.com.compose.di.appModuleimport ua.com.compose.extension.prefsimport ua.com.compose.gallery.di.galleryModuleclass MainApp : Application() {    override fun onCreate() {        super.onCreate()        prefs = getSharedPreferences("compose", Context.MODE_PRIVATE)        analytics = AnalyticsImpl(context = applicationContext)        remoteConfig = RemoteConfigImpl()        startKoin{            androidContext(this@MainApp)            modules(listOf(                    appModule,                    galleryModule            ))        }    }}