package ua.com.compose.viewimport android.content.Intentimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport ua.com.compose.view.main.MainActivityclass SplashActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)            val intent = Intent(this, MainActivity::class.java)            startActivity(intent)            finish()    }}